                            markdown += `## Summary\\n\\n${entry.summary || 'No summary available'}\\n\\n`;
                        } else if (entry.message) {
                            const role = entry.message.role || entry.type;
                            const timestamp = entry.timestamp ?
                                new Date(entry.timestamp).toLocaleTimeString() : '';
