# Simple multipart parsing for manifest field
boundary = self.headers.get('Content-Type', '').split('boundary=')[1] if 'boundary=' in self.headers.get('Content-Type', '') else None
if boundary:
    parts = post_data.decode('utf-8').split(f'--{boundary}')
