   457→</head>
   458→<body>
   459→    <div class="container">
   460→        <div class="header">
   461→            <h1>▓▓▓ CLAUDE CODE CONVERSATIONS ▓▓▓</h1>
   462→            <div class="stats" id="stats">
   463→                Loading archive data...
   464→            </div>
   465→        </div>
   466→
   467→        <div class="main-content">
   468→            <div class="conversation-list">
   469→                <div class="list-header">
   470→                    <span>[CONVERSATIONS]</span>
   471→                    <div class="list-controls">
   472→                        <button class="small-btn" id="showSnapshotsBtn">Snapshots</button>
   473→                        <button class="small-btn" id="showHiddenBtn">Hidden</button>
   474→                        <button class="small-btn" id="saveChangesBtn">Save</button>
   475→                    </div>
   476→                </div>
   477→                <div class="conversation-items" id="conversationList">
   478→                    <div class="loading">Loading conversations...</div>
   479→                </div>
   480→            </div>
   481→
   482→            <div id="conversationView" class="conversation-view">
   483→                <div class="view-header">
   484→                    <div id="viewTitle">[NO CONVERSATION SELECTED]</div>
   485→                    <div class="view-controls">
   486→                        <div class="control-group">
   487→                            <button class="btn active" id="focusedModeBtn">FOCUSED MODE</button>
   488→                            <button class="btn" id="detailedModeBtn">DETAILED MODE</button>
   489→                        </div>
   490→                        <div class="control-group">
   491→                            <button class="btn" id="exportBtn">EXPORT</button>
   492→                        </div>
   493→                    </div>
   494→                </div>
   495→                <div id="messages" class="messages">
   496→                    <div style="text-align: center; color: #888; margin-top: 50px;">Select a conversation to view</div>
   497→                </div>
   498→            </div>
   499→        </div>
   500→    </div>
   501→
   502→    <script>
   503→        // Load manifest from file instead of embedding
   504→        let manifest = null;
   505→        let currentConversation = null;
   506→        let viewMode = 'focused';
   507→        let showSnapshots = false;  // Toggle for showing snapshot files
   508→        let showHidden = false;  // Toggle for showing hidden conversations
   509→        let hiddenConversations = new Set();  // Track hidden conversations
   510→        let hasUnsavedChanges = false;  // Track if there are unsaved changes
   511→
   512→        // Initialize on startup
   513→        async function initializeViewer() {
   514→            try {
   515→                // Fetch manifest.json from the same directory
   516→                const response = await fetch('manifest.json');
   517→                manifest = await response.json();
   518→                hiddenConversations = new Set(manifest.hidden_conversations || []);
   519→                displayStats();
   520→                displayConversationList();
   521→            } catch (error) {
   522→                console.error('Failed to load manifest:', error);
   523→                document.getElementById('conversationList').innerHTML = '<div style="color: #ff0000;">Failed to load manifest.json</div>';
   524→            }
   525→        }
   526→
   527→        function displayStats() {
   528→            const stats = document.getElementById('stats');
   529→            stats.innerHTML = `
   530→                Project: <span style="color: #ffb000">${manifest.project_path}</span> |
   531→                Conversations: <span style="color: #ffb000">${manifest.conversation_count}</span> |
   532→                Total Messages: <span style="color: #ffb000">${manifest.total_messages}</span> |
   533→                Created: <span style="color: #888">${new Date(manifest.created_at).toLocaleString()}</span>
   534→            `;
   535→        }
   536→
   537→        function displayConversationList() {
   538→            const listContainer = document.getElementById('conversationList');
   539→            listContainer.classList.remove('loading');
   540→            listContainer.innerHTML = '';
   541→
   542→            manifest.conversations.forEach(conv => {
   543→                // Skip snapshots by default unless showing all
   544→                if (!showSnapshots && conv.conversation_type === 'snapshot') {
   545→                    return;
   546→                }
   547→
   548→                // Skip hidden conversations unless showing them
   549→                const isHidden = hiddenConversations.has(conv.session_id);
   550→                if (isHidden && !showHidden) {
   551→                    return;
   552→                }
   553→
   554→                const item = document.createElement('div');
   555→                item.className = 'conversation-item';
   556→                if (conv.conversation_type === 'snapshot') {
   557→                    item.className += ' snapshot';
   558→                }
   559→                if (isHidden) {
   560→                    item.className += ' hidden';
   561→                }
   562→                item.onclick = (event) => {
   563→                    // Don't load conversation if clicking on action buttons
   564→                    if (!event.target.matches('button')) {
   565→                        loadConversation(conv);
   566→                    }
   567→                };
   568→
   569→                // Determine the marker based on conversation type
   570→                let marker = '';
   571→                if (conv.conversation_type === 'post_compaction') {
   572→                    // Only mark true continuations
   573→                    marker = '<span class="continuation-marker">[CONTINUATION]</span> ';
   574→                } else if (conv.conversation_type === 'snapshot') {
   575→                    marker = '<span class="snapshot-marker">[SNAPSHOT]</span> ';
   576→                }
   577→                // Note: auto_linked and auto_linked_with_internal_compaction get no marker
   578→
   579→                // Format timestamps
   580→                const firstDate = conv.first_timestamp ? new Date(conv.first_timestamp) : null;
   581→                const lastDate = conv.last_timestamp ? new Date(conv.last_timestamp) : null;
   582→
   583→                const formatDateTime = (date) => {
   584→                    if (!date) return 'Unknown';
   585→                    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
   586→                };
   587→
   588→                // Create duration display if we have both dates
   589→                let durationText = '';
   590→                if (firstDate && lastDate && firstDate.getTime() !== lastDate.getTime()) {
   591→                    const durationMs = lastDate.getTime() - firstDate.getTime();
   592→                    const hours = Math.floor(durationMs / (1000 * 60 * 60));
   593→                    const minutes = Math.floor((durationMs % (1000 * 60 * 60)) / (1000 * 60));
   594→                    if (hours > 0) {
   595→                        durationText = ` (${hours}h ${minutes}m)`;
   596→                    } else if (minutes > 0) {
   597→                        durationText = ` (${minutes}m)`;
   598→                    }
   599→                }
   600→
   601→                item.innerHTML = `
   602→                    <div class="conversation-header">
   603→                        <div class="session-id">${marker}${conv.session_id.substring(0, 12)}...</div>
   604→                        <div class="conversation-title">${conv.title || 'Conversation'}</div>
   605→                    </div>
   606→                    <div class="conversation-meta">
   607→                        <div class="meta-line">
   608→                            <span class="meta-label">Messages:</span> ${conv.message_count}
   609→                            ${durationText}
   610→                        </div>
   611→                        <div class="meta-line">
   612→                            <span class="meta-label">Started:</span> ${formatDateTime(firstDate)}
   613→                        </div>
   614→                        ${lastDate && firstDate && lastDate.getTime() !== firstDate.getTime() ?
   615→                            `<div class="meta-line"><span class="meta-label">Last:</span> ${formatDateTime(lastDate)}</div>`
   616→                            : ''
   617→                        }
   618→                    </div>
   619→                    <div class="conversation-actions">
   620→                        <button onclick="toggleHideConversation('${conv.session_id}')">${isHidden ? 'SHOW' : 'HIDE'}</button>
   621→                        <button onclick="exportConversation('${conv.session_id}')">EXPORT</button>
   622→                    </div>
   623→                `;
   624→
   625→                listContainer.appendChild(item);
   626→            });
   627→        }
   628→
   629→        async function loadConversation(convInfo) {
   630→            // Update UI
   631→            document.querySelectorAll('.conversation-item').forEach(item => {
   632→                item.classList.remove('active');
   633→            });
   634→            event.currentTarget.classList.add('active');
   635→
   636→            // Update view title with enhanced metadata
   637→            const firstDate = convInfo.first_timestamp ? new Date(convInfo.first_timestamp) : null;
   638→            const lastDate = convInfo.last_timestamp ? new Date(convInfo.last_timestamp) : null;
   639→
   640→            const formatDateTime = (date) => {
   641→                if (!date) return 'Unknown';
   642→                return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
   643→            };
   644→
   645→            let titleText = `${convInfo.title || 'Conversation'}`;
   646→            let metaText = `${convInfo.message_count} messages`;
   647→
   648→            if (firstDate) {
   649→                metaText += ` • Started: ${formatDateTime(firstDate)}`;
   650→                if (lastDate && lastDate.getTime() !== firstDate.getTime()) {
   651→                    metaText += ` • Last: ${formatDateTime(lastDate)}`;
   652→                }
   653→            }
   654→
   655→            document.getElementById('viewTitle').innerHTML = `
   656→                <div class="view-title-main">${titleText}</div>
   657→                <div class="view-title-meta">[${convInfo.session_id.substring(0, 12)}...] ${metaText}</div>
   658→            `;
   659→
   660→            const messagesContainer = document.getElementById('messages');
   661→            messagesContainer.innerHTML = '<div class="loading">Loading conversation...</div>';
   662→
   663→            try {
   664→                // Load JSONL file
   665→                const response = await fetch(`conversations/${convInfo.session_id}.jsonl`);
   666→                const text = await response.text();
   667→                const lines = text.trim().split('\\n').filter(line => line);
   668→
   669→                currentConversation = lines.map(line => {
   670→                    try {
   671→                        return JSON.parse(line);
   672→                    } catch (e) {
   673→                        console.error('Failed to parse line:', e);
   674→                        return null;
   675→                    }
   676→                }).filter(entry => entry !== null);
   677→
   678→                displayMessages();
