   877→
   878→            if (entry.type === 'summary') {
   879→                header = '[SUMMARY - Continuation from previous conversation]';
   880→                mainContent = entry.summary || 'No summary available';
   881→            } else if (entry.type === 'system') {
   882→                header = '[SYSTEM]';
   883→                mainContent = entry.content || '';
   884→            } else if (entry.message) {
   885→                const role = entry.message.role?.toUpperCase() || entry.type?.toUpperCase();
   886→                const timestamp = entry.timestamp ? new Date(entry.timestamp).toLocaleTimeString() : '';
   887→                header = `[${role}] ${timestamp}`;
   888→
   889→                // Process message content
   890→                if (typeof entry.message.content === 'string') {
   891→                    mainContent = entry.message.content;
   892→                } else if (Array.isArray(entry.message.content)) {
   893→                    const textBlocks = [];
   894→                    entry.message.content.forEach(block => {
   895→                        if (block.type === 'text') {
   896→                            textBlocks.push(block.text || '');
   897→                        } else if (block.type === 'thinking') {
   898→                            textBlocks.push(`[Thinking] ${block.thinking || ''}`);
   899→                        }
   900→                    });
   901→                    mainContent = textBlocks.join('\\n\\n');
   902→                }
   903→            }
   904→
   905→            // Build the message HTML
   906→            let messageHTML = `<div class="message-header">${header}</div>`;
   907→            if (mainContent) {
   908→                messageHTML += `<div class="message-content">${escapeHtml(mainContent)}</div>`;
   909→            }
   910→
   911→            messageDiv.innerHTML = messageHTML;
   912→            container.appendChild(messageDiv);
   913→        }
