   915→        function processContentBlocks(blocks) {
   916→            const textBlocks = blocks
   917→                .filter(b => b.type === 'text' || b.type === 'thinking')
   918→                .map(b => b.text || b.thinking || '')
   919→                .join('\\n\\n');
   920→            return textBlocks;
   921→        }
   922→
   923→        function processToolResultContent(content) {
   924→            if (typeof content === 'string') {
   925→                return content;
   926→            } else if (Array.isArray(content)) {
   927→                // Handle tool_result blocks in the content
   928→                const results = content
   929→                    .filter(b => b.type === 'tool_result')
   930→                    .map(b => {
   931→                        if (typeof b.content === 'string') {
   932→                            return b.content;
   933→                        } else if (Array.isArray(b.content)) {
   934→                            // Handle nested content arrays
   935→                            return b.content
   936→                                .filter(c => c.type === 'text')
   937→                                .map(c => c.text || '')
   938→                                .join('\\n');
   939→                        }
   940→                        return '';
   941→                    })
   942→                    .join('\\n\\n');
   943→                return results || processContentBlocks(content);
   944→            }
   945→            return '';
   946→        }
