  1064→        async function saveChanges() {
  1065→            if (!hasUnsavedChanges) return;
  1066→
  1067→            try {
  1068→                // Update manifest with hidden conversations
  1069→                manifest.hidden_conversations = Array.from(hiddenConversations);
  1070→                manifest.user_metadata = manifest.user_metadata || {};
  1071→                manifest.user_metadata.last_modified = new Date().toISOString();
  1072→
  1073→                // Save updated manifest and repack archive
  1074→                const manifestBlob = new Blob([JSON.stringify(manifest, null, 2)],
  1075→                    { type: 'application/json' });
  1076→                const formData = new FormData();
  1077→                formData.append('manifest', manifestBlob, 'manifest.json');
  1078→
  1079→                // Show saving indicator
  1080→                const saveBtn = document.getElementById('saveChangesBtn');
  1081→                const originalText = saveBtn.textContent;
