  1083→                saveBtn.disabled = true;
  1084→
  1085→                const response = await fetch('/api/save-and-repack', {
  1086→                    method: 'POST',
  1087→                    body: formData
  1088→                });
  1089→
  1090→                if (response.ok) {
  1091→                    const result = await response.json();
  1092→                    hasUnsavedChanges = false;
  1093→                    updateSaveButton();
  1094→                    if (result.archive) {
  1095→                        alert(`Changes saved and archive repacked successfully!\\nArchive: ${result.archive}`);
  1096→                    } else {
  1097→                        alert(result.message || 'Changes saved successfully!');
  1098→                    }
  1099→                } else {
  1100→                    const errorResult = await response.json().catch(() => ({}));
  1101→                    throw new Error(errorResult.error || 'Failed to save changes');
  1102→                }
  1103→            } catch (error) {
  1104→                console.error('Save error:', error);
  1105→                alert('Failed to save changes. Changes are preserved in browser session only.');
  1106→            } finally {
  1107→                // Reset save button
  1108→                const saveBtn = document.getElementById('saveChangesBtn');
  1109→                saveBtn.disabled = false;
  1110→                updateSaveButton();
  1111→            }
  1112→        }
