    color: #ffffff;
}

.message {
    margin: 4px 0;
    display: flex;
    align-items: flex-start;
}

.message-prefix {
    flex-shrink: 0;
    width: 24px;
    text-align: center;
    user-select: none;
}

.message-content {
    flex: 1;
