"""Generator for terminal-style HTML viewer."""

from pathlib import Path
from typing import Any


class ViewerGenerator:
    """Generates a terminal-style HTML viewer for conversations."""

    def generate_viewer(self, manifest: dict[str, Any]) -> str:
        """Generate the complete HTML viewer.

        Args:
            manifest: The archive manifest data

        Returns:
            Complete HTML content as string
        """
        # No longer embedding manifest - will fetch from manifest.json file

        html_template = """<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Claude Code Conversations</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Cascadia Code', 'Fira Code', 'SF Mono', 'Consolas', 'Liberation Mono', 'Courier New', monospace;
            background: #0c0c0c;
            color: #00ff00;
            padding: 20px;
            line-height: 1.4;
            font-size: 14px;
        }

        .container {
            max-width: 100%;
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        .header {
            border: 1px solid #00ff00;
            padding: 10px;
            margin-bottom: 20px;
            background: #1a1a1a;
        }

        .header h1 {
            font-size: 16px;
            font-weight: normal;
            color: #ffb000;
        }

        .stats {
            margin-top: 10px;
            color: #888;
        }

        /* Main Layout */
        .main-content {
            display: flex;
            flex: 1;
            overflow: hidden;
            gap: 10px;
        }

        /* Conversation List */
        .conversation-list {
            width: 350px;
            border: 1px solid #00ff00;
            background: #0f0f0f;
            display: flex;
            flex-direction: column;
            overflow: hidden;
        }

        .list-header {
            background: #1a1a1a;
            padding: 10px;
            border-bottom: 1px solid #00ff00;
            color: #ffb000;
            display: flex;
            justify-content: space-between;
            align-items: center;
        }

        .list-controls {
            display: flex;
            gap: 5px;
        }

        .small-btn {
            background: #1a1a1a;
            border: 1px solid #666;
            color: #666;
            padding: 2px 6px;
            cursor: pointer;
            font-family: inherit;
            font-size: 10px;
        }

        .small-btn:hover {
            background: #222;
            color: #888;
        }

        .small-btn.active {
            background: #333;
            color: #ffb000;
            border-color: #ffb000;
        }

        .small-btn:disabled {
            background: #111;
            color: #444;
            border-color: #444;
            cursor: not-allowed;
        }

        .conversation-items {
            overflow-y: auto;
            flex: 1;
        }

        .conversation-item {
            padding: 10px;
            border-bottom: 1px solid #333;
            cursor: pointer;
            transition: background 0.2s;
        }

        .conversation-item:hover {
            background: #1a1a1a;
        }

        .conversation-item.active {
            background: #222;
            border-left: 3px solid #ffb000;
        }

        .conversation-item .conversation-header {
            margin-bottom: 8px;
        }

        .conversation-item .session-id {
            color: #00ff00;
            font-weight: bold;
            font-size: 11px;
        }

        .conversation-item .conversation-title {
            color: #fff;
            font-weight: normal;
            margin-top: 3px;
            font-size: 13px;
