        }

        .conversation-item .conversation-meta {
            color: #888;
            font-size: 11px;
        }

        .conversation-item .meta-line {
            margin-bottom: 2px;
        }

        .conversation-item .meta-label {
            color: #666;
            font-weight: bold;
        }

        .conversation-item .continuation-marker {
            color: #ffb000;
            font-size: 10px;
        }

        .conversation-item .snapshot-marker {
