        .conversation-actions button {
            background: none;
            border: 1px solid #444;
            color: #888;
            padding: 1px 4px;
            cursor: pointer;
            font-family: inherit;
            font-size: 9px;
            margin-right: 5px;
        }

        .conversation-actions button:hover {
            color: #ffb000;
            border-color: #ffb000;
        }

        /* Conversation View */
        .conversation-view {
            flex: 1;
            border: 1px solid #00ff00;
            background: #0f0f0f;
            display: flex;
            flex-direction: column;
            overflow: hidden;
        }

        .view-header {
            background: #1a1a1a;
            padding: 10px;
            border-bottom: 1px solid #00ff00;
        }

        .view-title-main {
            font-size: 16px;
            color: #fff;
            margin-bottom: 4px;
            font-weight: bold;
        }

        .view-title-meta {
            font-size: 12px;
            color: #888;
            font-weight: normal;
        }

        .view-controls {
            margin-top: 10px;
            display: flex;
            gap: 10px;
            align-items: center;
        }

        .control-group {
            display: flex;
            gap: 5px;
            align-items: center;
        }

        .btn {
            background: #1a1a1a;
            border: 1px solid #00ff00;
            color: #00ff00;
            padding: 5px 10px;
            cursor: pointer;
            margin-right: 10px;
            font-family: inherit;
            font-size: 12px;
        }

        .btn:hover {
            background: #222;
        }

        .btn.active {
            background: #00ff00;
            color: #0c0c0c;
        }

        /* Messages - Claude Code Style */
        .messages {
            padding: 20px;
            overflow-y: auto;
            flex: 1;
            font-family: 'SF Mono', 'Monaco', 'Cascadia Code', 'Fira Code', 'Consolas', 'Liberation Mono', monospace;
            line-height: 1.6;
