        }

        .sidechain-link {
            color: #8b5cf6;
            text-decoration: underline;
            cursor: pointer;
            font-size: 0.85em;
            margin-left: 32px;
            display: block;
            margin-top: 4px;
        }

        /* Tool groups - Claude Code style */
        .tool-group {
            margin: 8px 0;
        }

        .tool-group-header {
            display: flex;
            align-items: center;
            cursor: pointer;
            user-select: none;
            padding: 2px 0;
        }

        .tool-group-header .message-prefix {
            color: #00ff00;
        }

        .tool-group-header .message-prefix::before {
            content: "●";
        }

        .tool-group-summary {
            color: #00ff00;
            margin-left: 8px;
        }

        .tool-group-count {
            color: #666666;
            font-size: 0.9em;
            margin-left: 8px;
        }

        .tool-group-content {
            margin-left: 32px;
            padding: 4px 0;
            border-left: 1px dashed #333;
            padding-left: 12px;
            display: none;
        }

        .tool-group.expanded .tool-group-content {
            display: block;
        }

        .tool-message {
            margin: 4px 0;
            display: flex;
            align-items: flex-start;
        }

        .tool-message .message-prefix {
            color: #00ff00;
        }

        .tool-message .message-prefix::before {
            content: "→";
        }

        /* Individual tool display in detailed mode */
        .tool-detail {
            margin: 8px 0 8px 32px;
            padding: 8px;
            background: #0a0a0a;
            border-left: 1px solid #333;
            font-size: 12px;
            color: #888;
        }

        .tool-detail-header {
            color: #00ff00;
            margin-bottom: 4px;
        }

        .tool-detail-content {
            white-space: pre-wrap;
            max-height: 200px;
            overflow-y: auto;
        }

        /* Code blocks */
        .code-block {
            background: #1a1a1a;
            border: 1px solid #333;
            padding: 10px;
            margin: 10px 0;
            overflow-x: auto;
            font-size: 12px;
        }

        /* Tool blocks (individual tools in groups) */
        .tool-block {
            margin: 4px 0;
            margin-left: 24px;
        }

        .tool-block .tool-header {
            display: flex;
            align-items: center;
            gap: 8px;
            color: #888;
            font-size: 13px;
            padding: 2px 0;
        }

        .tool-block .tool-prefix {
            color: #00ff00;
        }

        .tool-block .tool-name {
            color: #888;
        }

        .tool-block .tool-details {
            margin-left: 24px;
            margin-top: 4px;
            padding: 8px;
            background: #0a0a0a;
            border-left: 1px solid #333;
            font-size: 12px;
            color: #666;
            max-height: 200px;
            overflow-y: auto;
        }

        .tool-block .tool-details pre {
            margin: 0;
            white-space: pre-wrap;
            word-wrap: break-word;
        }

        /* Loading */
        .loading {
            text-align: center;
            padding: 20px;
            color: #888;
        }

        /* Scrollbar */
        ::-webkit-scrollbar {
